import styles from "./Card.module.css";
import { NavLink } from "react-router-dom";
import { ADD_FAV, REMOVE_FAV } from "../../redux/action-typess";
import { connect } from "react-redux";
import { useState, useEffect } from "react";
// import { ADD_FAV, REMOVE_FAV } from "/../../redux/actions"
function Card(props) {
  const { character, onClose, REMOVE_FAV, ADD_FAV, myFavorites } = props;
  const [isFav, setIsFav] = useState(false);

  const handleFavorite = () => {
    if (isFav) {
      setIsFav(false);
      REMOVE_FAV(character.id);
    } else {
      setIsFav(true);
      ADD_FAV({ character });
    }
  };
  useEffect(() => {
    myFavorites.forEach((fav) => {
      if (fav.id === character.id) {
        setIsFav(true);
      }
    });
  }, [myFavorites]);

  return (
    <div className={styles.conteiner}>
      <button onClick={handleFavorite}>{isFav ? "‚ù§Ô∏è" : "ü§ç"}</button>
      <button className={styles.boton} onClick={() => onClose(character.id)}>
        X
      </button>
      <NavLink to={`/detail/${character.id}`}>
        <h2 className={styles.letraTitulo}>{character.name}</h2>
      </NavLink>
      <h2 className={styles.leter}>status:{character.status}</h2>
      <h2 className={styles.leter}>Species:{character.species}</h2>
      <h2 className={styles.leter}>Gender:{character.gender} </h2>
      {/* <h2>origen:{character.origen.name}</h2> */}
      <img
        className={styles.imgen}
        src={character.image}
        alt={character.name}
      />
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    myFavorites: state.myFavorites,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    ADD_FAV: (character) => {
      dispatch(ADD_FAV(character));
    },
    REMOVE_FAV: (id) => {
      dispatch(REMOVE_FAV(id));
    },
  };
};

export default connect(null, mapDispatchToProps)(Card);
