import styles from "./Card.module.css";
import { NavLink } from "react-router-dom";
export default function Card(props) {
  const {character, onClose} = props;
   return (
       <div className={styles.conteiner}>
        
        <div className={styles.div
      }>
         
         <button className={styles.boton} onClick={()=>onClose(character.id)}>X</button>
         <NavLink to="/detail/:id">
         <h2 className={styles.letraTitulo}>{character.name}</h2>
         </NavLink>
         <h2 className={styles.leter}>status:{character.status}</h2>
         <h2 className={styles.leter}>Species:{character.species}</h2>
         <h2 className={styles.leter}>Gender:{character.gender} </h2>
         {/* <h2>origen:{character.origen.name}</h2> */}
         <img className={styles.imgen} src={character.image} alt={character.name} />
         
         </div>
      </div>
   );
}
