import { NavLink, useNavigate } from "react-router-dom";
import { addFav, removeFav } from "../../redux/actions";
import { connect } from "react-redux";
import { useState, useEffect } from "react";
import styles from "./Card.module.css";

function Card({
  id,
  name,
  species,
  gender,
  image,
  onClose,
  addFav,
  removeFav,
  myFavorites,
}) {
  const navigate = useNavigate();
  const [isFav, setIsFav] = useState(false);

  const navHandler = () => {
    navigate(`/detail/${character.id}`);
  };

  useEffect(() => {
    myFavorites.forEach((fav) => {
      if (fav.id === id) {
        setIsFav(true);
      }
    });
  }, [myFavorites, character.id]);

  const handleFavorite = () => {
    if (isFav) {
      setIsFav(false);
      removeFav(id);
    } else {
      setIsFav(true);
      addFav({ id, name, species, gender, image, onClose });
    }
  };

  return (
    <div className={styles.conteiner}>
      {/* <button onClick={handleFavorite(character.id)}>{isFav ? "‚ù§Ô∏è" : "ü§ç"}</button> */}

      {isFav ? (
        <button onClick={() => handleFavorite(id)}>‚ù§Ô∏è</button>
      ) : (
        <button onClick={() => handleFavorite(id)}>ü§ç</button>
      )}

      <button className={styles.boton} onClick={() => onClose(id)}>
        X
      </button>
      <NavLink to={`/detail/${character.id}`}>
        <h2 className={styles.letraTitulo} onClick={navHandler}>
          {character.name}
        </h2>
      </NavLink>
      <h2 className={styles.leter}>status:{status}</h2>
      <h2 className={styles.leter}>Species:{species}</h2>
      <h2 className={styles.leter}>Gender:{gender} </h2>
      {/* <h2>origen:{character.origen.name}</h2> */}
      <img
        className={styles.imgen}
        src={character.image}
        alt={character.name}
      />
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    myFavorites: state.myFavorites,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    addFav: (character) => {
      dispatch(addFav(character));
    },
    removeFav: (id) => {
      dispatch(removeFav(id));
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Card);
